mapper.select:

SELECT
    cp.id as id,
    cp.channel_id,
    p.post_title
    FROM tbl_channel_post cp
    JOIN tbl_post p ON cp.id = p.id
WHERE cp.channel_id = #{channelId}
  AND cp.id > #{id}
LIMIT 1;


SELECT
    cp.id as id,
    cp.channel_id,
    p.post_title
FROM tbl_channel_post cp
         JOIN tbl_post p ON cp.id = p.id
WHERE cp.channel_id = #{channelId}
  AND cp.id < #{id}
ORDER BY cp.id DESC
LIMIT 1;

mapper.update:
update tbl_channel_post
set post_read_count = post_read_count + 1
where id = #{id}
