mapper.select:
SELECT *
FROM tbl_subscribe
WHERE member_id = #{memberId}
  AND channel_id = #{channelId}

mapper.update:
UPDATE tbl_subscribe
SET subscribe_status = #{status},
    updated_date = NOW()
WHERE member_id = #{memberId}
  AND channel_id = #{channelId}

mapper.select:
SELECT member_id
FROM tbl_channel
WHERE id = #{channelId}
